local function CreateSigmaParts()
    local folder = workspace:FindFirstChild("SigmaParts")
    if folder then folder:Destroy() end
    
    folder = Instance.new("Folder")
    folder.Name = "SigmaParts"
    folder.Parent = workspace
    
    for _, data in ipairs(partsData) do
        local part = Instance.new("Part")
        part.Name = data.name
        part.Size = data.size
        part.Position = data.pos
        part.Anchored = true
        part.CanCollide = true
        part.Material = Enum.Material.Neon
        part.Color = Color3.new(1, 0, 0)
        part.Transparency = 0
        part.Reflectance = 0.05
        
        if data.shape then
            part.Shape = data.shape
        end
        
        if data.ori then
            part.Orientation = data.ori
        end
        
        part.Parent = folder
    end
end

